option.value:
import {Metadata} from "next";
import {notFound} from "next/navigation";
import Image from "next/image";
import {getEventBySlug} from "@/api/generated";

type Props = {
    params: Promise<{
        slug: string;
    }>
}

const getEvent = (slug: string) => getEventBySlug({
    path: {
        slug
    },
    cache: "force-cache",
    next: {
        tags: [slug],
        revalidate: 60, // 1 minute
    }
});

export async function generateMetadata({params}: Props): Promise<Metadata> {
    const {slug} = await params;

    const {data: event, error} = await getEvent(slug);

    return {
        title: `Event Details - ${event?.title}`,
        description: event?.description || "Detailed information about the events.",
    }
}

const EventDetailsPage = async ({ params }: Props) => {
  const { slug } = await params;
  const res = await getEvent(slug);
  const event = res.data;

  if (!event || res.error) {
    notFound();
  }

  return (
    <div className="max-w-6xl mx-auto px-6 py-12">
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2">
          <div className="rounded-xl overflow-hidden shadow-lg bg-gradient-to-br from-slate-800 to-slate-900">
            <div className="relative w-full h-64 lg:h-96">
              <Image
                src={event.image || "/images/event1.png"}
                alt={event.title}
                fill
                style={{ objectFit: "cover" }}
                sizes="(max-width: 1024px) 100vw, 50vw"
              />
            </div>

            <div className="p-6">
              <h1 className="text-3xl md:text-4xl font-extrabold mb-2">{event.title}</h1>
              <p className="text-slate-300 mb-4">{event.overview || event.description}</p>

              <div className="flex flex-wrap gap-3 mb-6">
                <span className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-indigo-600/20 text-indigo-300 text-sm">{event.mode}</span>
                <span className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-emerald-600/20 text-emerald-300 text-sm">{event.audience}</span>
                {event.tags?.slice(0, 6).map((t, idx) => (
                  <span key={idx} className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-slate-700 text-slate-200 text-sm">{t}</span>
                ))}
              </div>

              <div className="prose prose-invert max-w-none">
                <h3 className="text-xl font-semibold mt-2">About</h3>
                <p>{event.description}</p>

                {event.agenda && event.agenda.length > 0 && (
                  <>
                    <h3 className="text-xl font-semibold mt-4">Agenda</h3>
                    <ul className="list-disc pl-6">
                      {event.agenda.map((item, i) => (
                        <li key={i} className="text-slate-200">{item}</li>
                      ))}
                    </ul>
                  </>
                )}

                <h3 className="text-xl font-semibold mt-4">Organizer</h3>
                <p>{event.organizer}</p>
              </div>
            </div>
          </div>
        </div>

        <aside className="space-y-6">
          <div className="rounded-xl p-5 bg-slate-900/60 border border-slate-800 shadow">
            <div className="flex items-center gap-3 mb-4">
              <Image src="/icons/calendar.svg" alt="date" width={20} height={20} />
              <div>
                <div className="text-slate-300 text-sm">Date</div>
                <div className="font-semibold">{event.date}</div>
              </div>
            </div>

            <div className="flex items-center gap-3 mb-4">
              <Image src="/icons/clock.svg" alt="time" width={20} height={20} />
              <div>
                <div className="text-slate-300 text-sm">Time</div>
                <div className="font-semibold">{event.time}</div>
              </div>
            </div>

            <div className="flex items-center gap-3 mb-4">
              <Image src="/icons/pin.svg" alt="location" width={20} height={20} />
              <div>
                <div className="text-slate-300 text-sm">Location</div>
                <div className="font-semibold">{event.venue || event.location}</div>
              </div>
            </div>

            <div className="flex justify-between items-center mt-4">
              <div className="text-sm text-slate-300">Tickets</div>
              <div>
                <button className="bg-indigo-600 hover:bg-indigo-500 px-4 py-2 rounded-md text-white">Get Tickets</button>
              </div>
            </div>
          </div>

          <div className="rounded-xl p-5 bg-slate-900/60 border border-slate-800 shadow">
            <h4 className="text-sm text-slate-300 mb-3">Organizer</h4>
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 rounded-full bg-slate-700 flex items-center justify-center text-white">{(event.organizer || "?").charAt(0)}</div>
              <div>
                <div className="font-semibold">{event.organizer}</div>
                <div className="text-sm text-slate-400">Host</div>
              </div>
            </div>
          </div>

          <div className="rounded-xl p-5 bg-slate-900/60 border border-slate-800 shadow">
            <h4 className="text-sm text-slate-300 mb-3">Share</h4>
            <div className="flex gap-2">
              <a className="px-3 py-2 bg-slate-700 hover:bg-slate-600 rounded-md">Twitter</a>
              <a className="px-3 py-2 bg-slate-700 hover:bg-slate-600 rounded-md">LinkedIn</a>
            </div>
          </div>
        </aside>
      </div>
    </div>
  );
};

export default EventDetailsPage;

